use super::*;

const DUMMY_BLOCK_BYTES: &str = "";

pub fn slot_to_hash(slot: u64) -> BlockHash {
    let mut hasher = pallas::crypto::hash::Hasher::<256>::new();
    hasher.input(&(slot as i32).to_le_bytes());
    hasher.finalize()
}

pub fn dummy_block_from_slot(slot: u64) -> RawBlock {
    let bytes = &hex::decode(DUMMY_BLOCK_BYTES).unwrap();
    let block = pallas::ledger::traverse::MultiEraBlock::decode(&bytes).unwrap();

    RawBlock {
        slot,
        hash: slot_to_hash(slot),
        era: block.era(),
        body: hex::decode(DUMMY_BLOCK_BYTES).unwrap(),
    }
}

pub fn empty_db() -> redb::WalStore {
    let mut wal = super::redb::WalStore::memory().unwrap();

    wal.initialize_from_origin().unwrap();

    wal
}

pub fn db_with_dummy_blocks(quantity: usize) -> redb::WalStore {
    let mut wal = empty_db();

    let blocks = (0..quantity).map(|x| dummy_block_from_slot(x as u64));
    wal.roll_forward(blocks).unwrap();

    wal
}
